import React, {Component} from 'react';

import styles from './header.module.scss';

export default class Header extends Component {
  state = {
    isBurgerVisible: false
  }

  onToggleBurger = () => {
    const {isBurgerVisible} = this.state;

    this.setState({
      isBurgerVisible: !isBurgerVisible
    },()=>{
      if (this.state.isBurgerVisible) {
        document.body.classList.add('lock');
      } else {
        document.body.classList.remove('lock');
      }
    })
  }

  render() {
    const {isBurgerVisible} = this.state;

    return (
      <header className="header">
        <div className="container">
          <div className="header-body">
            <a href="/" className="header-logo"><img src="/images/logo.svg" alt=""/></a>
            <nav className={isBurgerVisible ? 'header-menu active' : 'header-menu'}>
              <ul className="header-list">
                <li><a href="/" className="nav-item">Home</a></li>
                <li><a href="/" className="nav-item">About us</a></li>
              </ul>
            </nav>
            <div className={isBurgerVisible ? 'header-burger active' : 'header-burger'}
                 onClick={this.onToggleBurger}>
              <span/>
            </div>
          </div>
        </div>
      </header>
    )
  }
}
